import React, { useState } from 'react';
import { Text, View, TouchableOpacity, FlatList, SafeAreaView } from 'react-native';
import Lista from '../../components/Lista/Lista';
import estiloColecao from './estiloColecao';
import { MaterialIcons } from '@expo/vector-icons'
import { AntDesign } from '@expo/vector-icons'

function Colecao({ navigation, route }) {


const voltar = () => {
navigation.navigate('Inicial')
}

const [formData, setFormData] = useState({})

const updateInput = e => {
setFormData({
...formData,
[e.target.name]: e.target.value,
})
}
const handleSubmit = event => {
event.preventDefault()
sendEmail()
setFormData({
name: '',
email: '',
message: '',
})
}

return (
<View style={estiloColecao.container}>

	<View style={estiloColecao.header}>
		<TouchableOpacity onPress={voltar}>
			<MaterialIcons name="arrow-back" size={24} color="#fff" />
		</TouchableOpacity>
		<Text style={estiloColecao.texto}>Contato</Text>
	</View>

    <View style={estiloColecao.formulario}>

	<form onSubmit={handleSubmit}>
		<input
		type="text"
		name="name"
		placeholder="Name"
		onChange={updateInput}
		value={formData.name || ''}
		/>
		<input
		type="email"
		name="email"
		placeholder="Email"
		onChange={updateInput}
		value={formData.email || ''}
		/>
		<textarea
		type="text"
		name="message"
		placeholder="Message"
		onChange={updateInput}
		value={formData.message || ''}
		></textarea>
		<button type="submit">Submit</button>
	</form>
    </View>
</View>
)
}

export default Colecao;
